import React, { Component } from 'react'
import CourseDataService from '../service/CourseDataService';

//const INSTRUCTOR = 'in28minutes'

class AddUserComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            users: [],
            message: null
        }
        this.deleteUserClicked = this.deleteUserClicked.bind(this)
        this.updateUserClicked = this.updateUserClicked.bind(this)
        this.addUserClicked = this.addUserClicked.bind(this)
        this.refreshUsers = this.refreshUsers.bind(this)
    }

    componentDidMount() {
        this.refreshUsers();
    }

    refreshUsers() {
        CourseDataService.retrieveAllUsers(this.props.match.params.id)
            .then(
                response => {
                    //console.log(response);
                    this.setState({ users: response.data })
                }
            )
    }

    deleteUserClicked(id) {
        CourseDataService.deleteUser(this.props.match.params.id, id)
            .then(
                response => {
                    this.setState({ message: `Delete of user ${id} Successful` })
                    this.refreshUsers()
                }
            )

    }

    addUserClicked(id,idUser) {
        this.props.history.push(`/courses/${id}/addUser/${idUser}`)
    }

    updateUserClicked(id) {
        console.log('update ' + id)
        this.props.history.push(`/users/${id}`)
    }

    render() {
        console.log('render')
        return (
            <div className="container">
                <h3>All Userss</h3>
                {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Nome</th>
                                <th>Update</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.users.map(
                                    user =>
                                        <tr key={user.id}>
                                            <td>{user.id}</td>
                                            <td>{user.nome}</td>
                                            <td><button className="btn btn-success" onClick={() => this.updateUserClicked(user.id)}>Update</button></td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteUserClicked(user.id)}>Delete</button></td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addUserClicked}>Add</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default AddUserComponent
